// Reload cached pages when online
window.applicationCache.addEventListener("updateready",function(){window.applicationCache.swapCache();window.location.reload()},!1);
// Disable jQuery Mobile Ajax features
$(document).bind("mobileinit",function(){$.mobile.ajaxEnabled=!1});
// Open all links in the same window
$("div[data-role=content] a").live("click",function(a){"_blank"!==$(this).attr("target")&&(a.preventDefault(),window.location.href=$(this).attr("href"))});
// Randomly alternate background images
$(document).live("pageinit",function(){1===Math.floor(2*Math.random())&&$("div[data-role=page]").attr("data-bg","black")});
// Display the splash screen once per session
$(document).live("pageinit",function(){var a=$("#splashscreen");0<a.length&&null===$.cookie("splashscreen")&&($.cookie("splashscreen","true"),a.css("display","block"),setTimeout('$("#splashscreen").css("display","none")',3E3))});
// Set width and height for full screen YouTube videos
$(document).live("pageinit",function(){$(window).resize(function(){var a=$("*[data-fullscreen=true] .ui-content"),b=a.find(".youtube");a.css("height",$(window).height()-41+"px");b.css("width",$(window).width()+"px");b.css("height",$(window).height()-41+"px")});$(window).resize()});